import React from "react";
import styled from "styled-components";
import { Input, Statistic, Spin, Descriptions } from "antd";
import { useSelector, useDispatch } from "react-redux";

import {
  selectRandomActivity,
  selectRandomActivityStatus
} from "./dashboardSlice";
import StatusEnum from "../../constants/StatusEnum";

const Modal = () => {
  const activityToDisplay = useSelector(selectRandomActivity);
  const status = useSelector(selectRandomActivityStatus);

  if (status !== StatusEnum.succeeded) {
    return <Spin size="large" />;
  }
  return (
    <div>
      {Object.entries(activityToDisplay).map(([key, value]) => (
        <Statistic key={key} title={key} value={value} />
      ))}
      <Descriptions title="User Info">
        <Descriptions.Item label="UserName">Zhou Maomao</Descriptions.Item>
        <Descriptions.Item label="Telephone">1810000000</Descriptions.Item>
        <Descriptions.Item label="Live">Hangzhou, Zhejiang</Descriptions.Item>
        <Descriptions.Item label="Remark">empty</Descriptions.Item>
        <Descriptions.Item label="Address">
          No. 18, Wantang Road, Xihu District, Hangzhou, Zhejiang, China
        </Descriptions.Item>
      </Descriptions>
    </div>
  );
};

const StyledModal = styled(Modal)``;

export default Modal;
