import React from "react";
import styled from "styled-components";
import { Input, Statistic, Spin, Image } from "antd";
import { useSelector, useDispatch } from "react-redux";

import {
  selectRandomActivity,
  selectRandomActivityStatus
} from "./dashboardSlice";
import StatusEnum from "../../constants/StatusEnum";

const Modal = () => {
  const selectedActivity = useSelector(selectRandomActivity);
  const status = useSelector(selectRandomActivityStatus);

  if (status !== StatusEnum.succeeded) {
    return <Spin size="large" />;
  }
  const {
    accessibility,
    activity,
    imageUrl,
    participants,
    price,
    type
  } = selectedActivity;
  return (
    <>
      <ImageWrapper>
        <Image src={imageUrl} />
      </ImageWrapper>
      <Activity title="Activity" value={activity} />
      <Row gutter={16}>
        <Col span={12}>
          <Statistic title="Active Users" value={112893} />
        </Col>
        <Col span={12}>
          <Statistic
            title="Account Balance (CNY)"
            value={112893}
            precision={2}
          />
          <Button style={{ marginTop: 16 }} type="primary">
            Recharge
          </Button>
        </Col>
      </Row>
      <Type>
        <Statistic title="Type" value={type} />
      </Type>
      <Price title="Price" value={price} />
      <Participants title="Participants" value={participants} />
      <Accessibility title="Accessibility" value={accessibility} />
    </>
  );
};

const StyledModal = styled(Modal)`
  display: grid;
  grid-template-areas: "image image" "activity activity" "type price" participants accessibility";
  justify-items: center;
  align-items: center;
  grid-template-columns: 1fr 1fr;
`;

const ImageWrapper = styled.div`
  grid-area: image;
`;

const Activity = styled(Statistic)`
  grid-area: activity;
`;
const Type = styled.div`
  grid-area: type;
`;
const Price = styled.div`
  grid-area: price;
`;
const Accessibility = styled(Statistic)`
  grid-area: accessibility;
`;
const Participants = styled(Statistic)`
  grid-area: participants;
`;
export default StyledModal;
