import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import styles from "../counter/Counter.module.css";

import { Input, Button } from "antd";
import DashboardTable from "./DashboardTable";
import DashboardCard from "./DashboardCard";

import {
  fetchActivities,
  fetchActivitiesByType,
  selectActivities
} from "./dashboardSlice";

const { Search } = Input;

export default function Dashboard() {
  const dispatch = useDispatch();
  const activities = useSelector(selectActivities);

  const dashboardStatus = useSelector(state => state.dashboard.status);

  useEffect(() => {
    if (dashboardStatus === "idle") {
      dispatch(fetchActivities());
    }
  }, [dashboardStatus, dispatch]);
  //<DashboardTable data={activities} />
  /*
{activities.slice(0, 10).map(activity => (
          <DashboardCard data={activity} />
        ))}
  */
  return (
    <>
      <div className="logo">
        <h1>Logo</h1>
        <h3>Welcome, an Other</h3>
      </div>
      <div className="refresh">
        <Button>Click</Button>
      </div>
      <div className="searchbar">
        <Search
          placeholder="Search by Type or Name"
          onSearch={value => dispatch(fetchActivitiesByType(value))}
          enterButton
        />
      </div>
      <div className="table">
        <DashboardTable data={activities} />
      </div>
    </>
  );
}
