import React from "react";
import styled from "styled-components";
import { Input, Statistic, Spin, Image } from "antd";
import { useSelector, useDispatch } from "react-redux";

import {
  selectRandomActivity,
  selectRandomActivityStatus
} from "./dashboardSlice";
import StatusEnum from "../../constants/StatusEnum";

const Modal = () => {
  const selectedActivity = useSelector(selectRandomActivity);
  const status = useSelector(selectRandomActivityStatus);

  if (status !== StatusEnum.succeeded) {
    return <Spin size="large" />;
  }
  const { accessibility, activity, imageUrl, price, type } = selectedActivity;
  return (
    <>
      <ImageWrapper>
        <Image src={imageUrl} />
      </ImageWrapper>
      <Activity title="Activity" value={activity} />
      <Type title="Type" value={type} />
      <Price title="Price" value={price} />
    </>
  );
};

const StyledModal = styled(Modal)`
  display: grid;
  grid-template-areas:
    "image image"
    "activity activity"
    "type price"
    "participants accessibility";
  justify-items: center;
  align-items: center;
`;

const ImageWrapper = styled.div`
  grid-area: image;
`;

const Activity = styled(Statistic)`
  grid-area: activity;
`;
const Type = styled(Statistic)`
  grid-area: type;
`;
const Price = styled(Statistic)`
  grid-area: price;
`;
export default StyledModal;
