import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import styled from "styled-components";

import { Input, Modal } from "antd";
import DashboardTable from "./DashboardTable";
import DashBoardModal from "./DashBoardModal";
import Button from "../../components/Button";
import {
  fetchActivity,
  fetchActivities,
  selectActivities,
  setSearchedValue
} from "./dashboardSlice";

const { Search } = Input;

export default function Dashboard() {
  const dispatch = useDispatch();
  const [showModal, setShowModal] = useState(false);
  const activities = useSelector(selectActivities);
  const dashboardStatus = useSelector(state => state.dashboard.status);

  useEffect(() => {
    if (dashboardStatus === "idle") {
      dispatch(fetchActivities());
    }
  }, [dashboardStatus, dispatch]);

  const handleRandomActivity = () => {
    dispatch(fetchActivity());
    setShowModal(true);
  };

  return (
    <>
      <Header>
        <span>Quarantine ideas</span>
      </Header>
      <RefreshButton shape="flat" onClick={() => dispatch(fetchActivities())}>
        Refresh ideas
      </RefreshButton>
      <SearchBar
        placeholder="Search by Type or Name"
        onSearch={value =>
          dispatch(setSearchedValue(value.toLocaleLowerCase()))
        }
        enterButton
      />
      <div className="generator">
        <Button shape="flat" onClick={() => handleRandomActivity()}>
          Generate
        </Button>
      </div>
      <div className="grid">
        <DashboardTable data={activities} />
      </div>
      <Modal
        visible={showModal}
        onOk={() => setShowModal(false)}
        onCancel={() => setShowModal(false)}
      >
        <DashBoardModal />
      </Modal>
    </>
  );
}

const SearchBar = styled(Search)`
  width: 20em;
  border-radius: 90px;
  grid-area: searchbar;
  margin-top: 3em;
  margin-bottom: 3em;
`;

const Header = styled.div`
  grid-area: header;
  display: grid;
  font-size: 2em;
  margin-top: 3em;
  width: 100%;
`;

const RefreshButton = styled(Button)`
  grid-area: refresh;
  margin-top: 3em;
  margin-bottom: 3em;
`;
