import React from "react";
import styled from "styled-components";
import { Input, Statistic, Spin, Descriptions } from "antd";
import { useSelector, useDispatch } from "react-redux";

import {
  selectRandomActivity,
  selectRandomActivityStatus
} from "./dashboardSlice";
import StatusEnum from "../../constants/StatusEnum";

const Modal = () => {
  const activityToDisplay = useSelector(selectRandomActivity);
  const status = useSelector(selectRandomActivityStatus);

  if (status !== StatusEnum.succeeded) {
    return <Spin size="large" />;
  }
  return (
    <>
      <ImageWrapper>
        <Image />
      </ImageWrapper>
    </>
  );
};

const StyledModal = styled(Modal)`
  display: grid;
  grid-template-areas:
    "image image"
    "activity activity"
    "type price"
    "participants accessibility";
  justify-items: center;
  align-items: center;
`;

const ImageWrapper = styled.div`
  grid-area: image;
`;
export default StyledModal;
