import React, { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { fetchActivities, selectActivities } from "./dashboardSlice";

import { Table, Tag, Space } from "antd";

const columns = [
  {
    title: "Activity",
    dataIndex: "activity",
    key: "activity",
    render: text => <a>{text}</a>,
    sorter: (a, b) => a.activity.localeCompare(b.activity)
  },
  {
    title: "Type",
    dataIndex: "type",
    key: "type",
    sorter: (a, b) => a.type.localeCompare(b.type)
  },
  {
    title: "Participants",
    dataIndex: "participants",
    key: "participants",
    sorter: (a, b) => a.participants - b.participants
  },
  {
    title: "Price",
    dataIndex: "price",
    key: "price",
    sorter: (a, b) => a.price - b.price
  },
  {
    title: "Image",
    dataIndex: "imageUrl",
    key: "imageUrl"
  }
];

export default function DashboardTable() {
  const data = useSelector(selectActivities);
  const dispatch = useDispatch();

  const dashboardStatus = useSelector(state => state.dashboard.status);

  useEffect(() => {
    if (dashboardStatus === "idle") {
      dispatch(fetchActivities());
    }
  }, [dashboardStatus, dispatch]);
  return <Table columns={columns} dataSource={data} />;
}
