import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import styles from "../counter/Counter.module.css";
import styled from "styled-components";

import { Input, Modal } from "antd";
import DashboardTable from "./DashboardTable";
import Button from "../../components/Button";
import {
  fetchActivity,
  fetchActivities,
  selectActivities,
  setSearchedValue,
  selectRandomActivity,
  selectRandomActivityStatus
} from "./dashboardSlice";

const { Search } = Input;

export default function Dashboard() {
  const dispatch = useDispatch();
  const [showModal, setShowModal] = useState(false);
  const activities = useSelector(selectActivities);
  const randomActivityStatus = useSelector(selectRandomActivityStatus);
  const randomActivity = useSelector(selectRandomActivity);
  const dashboardStatus = useSelector(state => state.dashboard.status);

  useEffect(() => {
    if (dashboardStatus === "idle") {
      dispatch(fetchActivities());
    }
  }, [dashboardStatus, dispatch]);

  /*
<div className="favourite">
        <Button shape="flat" onClick={handleRefreshActivities}>
          Your Favourites
        </Button>
      </div>
  */

  const handleRandomActivity = () => {
    dispatch(fetchActivity());
    setShowModal(true);
  };

  const displayItemInfo = () => {
    if (randomActivityStatus === "succeeded") {
      return (
        <>
          <span>Idea</span>:{randomActivity.activity}
        </>
      );
    }
  };

  return (
    <>
      <div className="header">
        <span>Quarantine ideas</span>
      </div>

      <div className="refresh">
        <Button shape="flat" onClick={() => dispatch(fetchActivities())}>
          Refresh ideas
        </Button>
      </div>
      <div className="searchbar">
        <StyledSearch
          placeholder="Search by Type or Name"
          onSearch={value =>
            dispatch(setSearchedValue(value.toLocaleLowerCase()))
          }
          enterButton
        />
      </div>
      <div className="generator">
        <Button shape="flat" onClick={() => handleRandomActivity()}>
          Generate
        </Button>
      </div>
      <div className="grid">
        <DashboardTable data={activities} />
      </div>
      <Modal
        visible={showModal}
        onOk={() => setShowModal(false)}
        onCancel={() => setShowModal(false)}
      >
        {displayItemInfo()}
      </Modal>
    </>
  );
}

const StyledSearch = styled(Search)`
  width: 20em;
  border-radius: 90px;
`;
