import React, { useState, useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import styles from "../counter/Counter.module.css";

import { Input } from "antd";
import DashboardTable from "./DashboardTable";
import DashboardCard from "./DashboardCard";
import Button from "../../components/Button";
import {
  fetchActivities,
  selectActivities,
  setSearchedValue
} from "./dashboardSlice";
import { SyncOutlined } from "@ant-design/icons";

const { Search } = Input;

export default function Dashboard() {
  const dispatch = useDispatch();
  const activities = useSelector(selectActivities);
  const [searchedActivities, setSearchedActivities] = useState(activities);

  const dashboardStatus = useSelector(state => state.dashboard.status);

  useEffect(() => {
    if (dashboardStatus === "idle") {
      dispatch(fetchActivities());
    }
  }, [dashboardStatus, dispatch]);

  const handleRefreshActivities = () => {
    dispatch(fetchActivities());
  };

  const handleOnSearch = value =>
    value && value !== ""
      ? setSearchedActivities(
          activities.filter(
            record =>
              record.activity.includes(value) || record.type.includes(value)
          )
        )
      : setSearchedActivities(activities);

  return (
    <>
      <div className="logo">
        <h1>Quarantine ideas</h1>
        <h3>Welcome, an Other</h3>
      </div>
      <div className="favourite">
        <Button shape="flat" onClick={handleRefreshActivities}>
          Your Favourites
        </Button>
      </div>
      <div className="refresh">
        <Button shape="flat" onClick={() => dispatch(fetchActivities())}>
          Refresh ideas
        </Button>
      </div>
      <div className="searchbar">
        <Search
          placeholder="Search by Type or Name"
          onSearch={value => dispatch(setSearchedActivities(value))}
          enterButton
        />
      </div>
      <div className="generator">
        <Button shape="rounded">G</Button>
      </div>
      <div className="grid">
        <DashboardTable data={searchedActivities} />
      </div>
    </>
  );
}
