import React from "react";
import styled from "styled-components";
import { Input, Statistic, Spin } from "antd";
import { useSelector, useDispatch } from "react-redux";

import {
  selectRandomActivity,
  selectRandomActivityStatus
} from "./dashboardSlice";
import StatusEnum from "../../constants/StatusEnum";

const Modal = () => {
  const activityToDisplay = useSelector(selectRandomActivity);
  const status = useSelector(selectRandomActivityStatus);

  if (status !== StatusEnum.succeeded) {
    return <Spin size="large" />;
  }
  const titles = ["Idea", "Type", "Participants", "Price"];

  return (
    <div>
      {Object.values(activityToDisplay).map((attribute, index) => (
        <Statistic key={index} title={titles[index]} value={attribute} />
      ))}
    </div>
  );
};

export default Modal;
