import React from "react";
import { Button as AntdButton } from "antd";
import styled from "styled-components";

const Button = ({ children, onClick, shape = "long", ...otherProps }) => (
  <AntdButton onClick={onClick} shape={shape} {...otherProps}>
    {children}
  </AntdButton>
);

/*
background: #fbeaea;
border-radius: 50%;
box-shadow: -5px 5px 10px #e7d7d7, 5px -5px 10px #fffdfd;
*/

const getStyleBasedOnShape = shape => {
  console.log(type);
  switch (shape) {
    case "rounded":
      return `
        border-radius: 50%;
        `;
    default:
      return `
        border-radius: 90px;
        `;
  }
};

const StyledButton = styled(Button)`
  ${({ shape }) => getStyleBasedOnShape(shape)}
  border: none;
  background: linear-gradient(225deg, #4ff0f3, #43cacc);
  box-shadow: -7px 7px 12px #e7d7d7, 7px -7px 12px #fffdfd;
  width: 100px;
  height: 100px;
  &:active {
    border-radius: 50%;
    background: #4ae0e3;
    box-shadow: inset -7px 7px 12px #38aaad, inset 7px -7px 12px #5cffff;
  }
  &:hover {
    border-radius: 50%;
    background: linear-gradient(225deg, #4ff0f3, #43cacc);
  }
`;

export default StyledButton;
