import React from "react";
import { Button as AntdButton } from "antd";
import styled from "styled-components";

const Button = ({ children, onClick, type = "long", ...otherProps }) => (
  <AntdButton onClick={onClick} type={type} {...otherProps}>
    {children}
  </AntdButton>
);

/*
background: #fbeaea;
border-radius: 50%;
box-shadow: -5px 5px 10px #e7d7d7, 5px -5px 10px #fffdfd;
*/

const getStyleBasedOnType = type => {
  switch (type) {
    case "rounded":
      return `
        border-radius: 50%;
        `;
    default:
      return `
        border-radius: 90px;
        `;
  }
};

const StyledButton = styled(Button)`
border: none;
border-radius: 50%;
background: linear-gradient(225deg, #4ff0f3, #43cacc);
box-shadow:  -7px 7px 12px #e7d7d7, 7px -7px 12px #fffdfd;
height: 100px;
width: 100px;
  &:active {
    border-radius: 50%;
    background: #4ae0e3;
    box-shadow: inset -7px 7px 12px #38aaad, inset 7px -7px 12px #5cffff;
  }
  &:hover {
    border-radius: 50%;
    background: linear-gradient(225deg, #4ff0f3, #43cacc);
    ${({ type }) => getStyleBasedOnType(type)}
`;

export default StyledButton;
