import { createSlice } from "@reduxjs/toolkit";

import { activitiesData } from "./dashboardData";

const StatusEnum = ["idle", "loading", "succeeded", "failed"];

export const dashboardSlice = createSlice({
  name: "dashboard",
  initialState: {
    activities: [],
    status: StatusEnum.idle,
    error: null
  },
  reducers: {
    fetchActivities: state => {
      // Redux Toolkit allows us to write "mutating" logic in reducers. It
      // doesn't actually mutate the state because it uses the Immer library,
      // which detects changes to a "draft state" and produces a brand new
      // immutable state based off those changes
      state.activities = activitiesData;
    },
    incrementByAmount: (state, action) => {
      state.value += action.payload;
    }
  },
  extraReducers: {
    [fetchActivities.pending]: (state, action) => {
      state.status = "loading";
    },
    [fetchActivities.fulfilled]: (state, action) => {
      state.status = "succeeded";
      state.activities = state.activities.concat(action.payload);
    },
    [fetchActivities.rejected]: (state, action) => {
      state.status = "failed";
      state.error = action.error.message;
    }
  }
});

export const { fetchActivities, incrementByAmount } = dashboardSlice.actions;

// The function below is called a thunk and allows us to perform async logic. It
// can be dispatched like a regular action: `dispatch(incrementAsync(10))`. This
// will call the thunk with the `dispatch` function as the first argument. Async
// code can then be executed and other actions can be dispatched
export const fetchPosts = createAsyncThunk(
  "dashboard/fetchActivities",
  async () => {
    const response = await client.get("list/10");

    return response;
  }
);

// The function below is called a selector and allows us to select a value from
// the state. Selectors can also be defined inline where they're used instead of
// in the slice file. For example: `useSelector((state) => state.counter.value)`
export const selectActivities = state => state.dashboard.activities;

export default dashboardSlice.reducer;
