import React from "react";
import { Button as AntdButton } from "antd";
import styled from "styled-components";

const Button = ({ children, onClick, shape = "long", ...otherProps }) => (
  <AntdButton onClick={onClick} shape={shape} {...otherProps}>
    {children}
  </AntdButton>
);

/*
background: #fbeaea;
border-radius: 50%;
box-shadow: -5px 5px 10px #e7d7d7, 5px -5px 10px #fffdfd;
*/

const getStyleBasedOnShape = shape => {
  switch (shape) {
    case "rounded":
      return `
        border-radius: 50%;
        height: 3em;
        width: 3em;
        `;
    case "flat":
      return `
          border-radius: 90px;
          height: 3em;
          min-width: 10em;
          `;
    default:
      return `
        border-radius: 90px;
        `;
  }
};
/*
const StyledButton = styled(Button)`
  ${({ shape }) => getStyleBasedOnShape(shape)}
  border: none;
  background: #fbeaea;
  box-shadow: 5px 5px 10px #bfa793, -5px -5px 10px #ffe1c7;
  &:active {
    box-shadow: inset -7px 7px 12px #38aaad, inset 7px -7px 12px #5cffff;
  }
  &:hover {
  }
`;
*/
export default StyledButton;
