import React from "react";
import { Button as AntdButton } from "antd";
import styled from "styled-components";

const Button = ({ children, onClick, ...otherProps }) => (
  <AntdButton onClick={onClick} {...otherProps}>
    {children}
  </AntdButton>
);

/*
background: #fbeaea;
border-radius: 50%;
box-shadow: -5px 5px 10px #e7d7d7, 5px -5px 10px #fffdfd;
*/

const StyledButton = styled(Button)`
border: none;

border-radius: 50%;
background: linear-gradient(225deg, #4ff0f3, #43cacc);
box-shadow:  -7px 7px 12px #e7d7d7, 7px -7px 12px #fffdfd;
height: 100px;
  width: 100px;
  &:hover {
    border-radius: 50%;
    background: #4ae0e3;
    box-shadow: inset -7px 7px 12px #e7d7d7, inset 7px -7px 12px #fffdfd;
  }
  &:active {
    opacity: 1;
    box-shadow: inset -5px 5px 10px #e7d7d7,
      inset 5px -5px 10px #fffdfd;
`;

export default StyledButton;
