import React from "react";
import styled from "styled-components";
import { Input, Statistic, Spin } from "antd";
import { useSelector, useDispatch } from "react-redux";

import {
  selectRandomActivity,
  selectRandomActivityStatus
} from "./dashboardSlice";
import StatusEnum from "../../constants/StatusEnum";

const Modal = () => {
  const activityToDisplay = useSelector(selectRandomActivity);
  const status = useSelector(selectRandomActivityStatus);

  if (status !== StatusEnum.succeeded) {
    return <Spin size="large" />;
  }

  return (
    <div>
      <Statistic title="Idea" value={activityToDisplay.activity} />
      <Statistic title="Type" value={activityToDisplay.type} />
      <Statistic title="Idea" value={activityToDisplay.participants} />
    </div>
  );
};

export default Modal;
