import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import { get } from "../../api/client";

const StatusEnum = {
  idle: "idle",
  loading: "loading",
  succeeded: "succeeded",
  failed: "failed"
};

// Thunk action creators
export const fetchActivities = createAsyncThunk(
  "dashboard/fetchActivities",
  async () => {
    const response = await get("/list");
    return response;
  }
);

export const fetchActivitiesByType = createAsyncThunk(
  "dashboard/fetchActivitiesByType",
  async type => {
    const response = await get(`?type=${type}`);
    return response;
  }
);

// Slice
export const dashboardSlice = createSlice({
  name: "dashboard",
  initialState: {
    activities: [],
    searchedValue: [],
    status: StatusEnum.idle,
    error: null
  },
  // Dashboard reducers
  reducers: {
    search: (state, action) => {
      const query = action.payload;
      return state.activities.filter(
        activity =>
          activity.activity.includes(query) || activity.type.includes(query)
      );
    },
    updateActivities: (state, action) => {
      state.activities = action.payload;
    }
  },
  // Thunk reducers
  extraReducers: {
    [fetchActivities.pending]: (state, action) => {
      state.status = "loading";
    },
    [fetchActivities.fulfilled]: (state, action) => {
      state.status = "succeeded";
      state.activities = action.payload;
    },
    [fetchActivities.rejected]: (state, action) => {
      state.status = "failed";
      state.error = action.error.message;
    },
    [fetchActivitiesByType.pending]: (state, action) => {
      state.status = "loading";
    },
    [fetchActivitiesByType.fulfilled]: (state, action) => {
      state.status = "succeeded";
      state.activities = action.payload;
    },
    [fetchActivitiesByType.rejected]: (state, action) => {
      state.status = "failed";
      state.error = action.error.message;
    }
  }
});

// Actions
export const { incrementByAmount } = dashboardSlice.actions;

// Selectors
export const selectActivities = state => {
  return state.activities.filter(
    activity =>
      activity.activity.includes(query) || activity.type.includes(query)
  );
};

export default dashboardSlice.reducer;
