import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import { get } from "../../api/client";

const StatusEnum = {
  idle: "idle",
  loading: "loading",
  succeeded: "succeeded",
  failed: "failed"
};

// Thunk action creators
export const fetchActivities = createAsyncThunk(
  "dashboard/fetchActivities",
  async () => {
    const response = await get("/list");
    return response;
  }
);

export const fetchActivitiesByType = createAsyncThunk(
  "dashboard/fetchActivitiesByType",
  async type => {
    const response = await get(`?type=${type}`);
    return response;
  }
);

// Slice
export const dashboardSlice = createSlice({
  name: "dashboard",
  initialState: {
    activities: [],
    searchedValue: null,
    status: StatusEnum.idle,
    error: null
  },
  // Dashboard reducers
  reducers: {
    setSearchedValue: (state, action) => {
      console.log(action.payload);
      state.searchedValue = action.payload;
    }
  },
  // Thunk reducers
  extraReducers: {
    [fetchActivities.pending]: (state, action) => {
      state.status = "loading";
      state.searchedValue = null;
    },
    [fetchActivities.fulfilled]: (state, action) => {
      state.status = "succeeded";
      state.activities = action.payload;
    },
    [fetchActivities.rejected]: (state, action) => {
      state.status = "failed";
      state.error = action.error.message;
    }
  }
});

// Actions
export const { setSearchedValue } = dashboardSlice.actions;

// Selectors
export const selectActivities = state => {
  const { activities, searchedValue } = state;
  if (searchedValue) {
    return state.activities.filter(
      activity =>
        activity.activity.includes(searchedValue) ||
        activity.type.includes(searchedValue)
    );
  }
  return activities;
};

export default dashboardSlice.reducer;
